INSERT INTO "grupoUsuario_permissao" ("grupoUsuario_id", permissao_id)
SELECT 1, id FROM permissao WHERE key IN (
    'remedio:read', 'remedio:create', 'remedio:update', 'remedio:delete',
    'laboratorio:read', 'laboratorio:create', 'laboratorio:update', 'laboratorio:delete',
    'usuario:read', 'usuario:create', 'usuario:update', 'usuario:delete'
);
INSERT INTO "grupoUsuario_permissao" ("grupoUsuario_id", permissao_id)
SELECT 2, id FROM permissao WHERE key IN (
    'remedio:read', 'remedio:create', 'remedio:update', 'remedio:delete', 'laboratorio:read'
);
